sys_script_client.script:
function onLoad() {

    var scope = 'x_opt_ecm_case';

    console.log("Attaching Form Validators...");
    // Form fields to enforce
    var fields = [{
        name: "member_number",
        maxLen: 9,
        type: "numeric"
    }, {
        name: "member_group_number",
        maxLen: 7,
        type: "numeric"
    }, {
        name: "member_policy_number",
        maxLen: 7,
        type: "alpha-numeric"
    }, {
        name: "member_ssn",
        maxLen: 9,
        type: "numeric"
    }, {
        name: "contact_number",
        maxLen: 9,
        type: "numeric"
    }, {
        name: "contact_group_number",
        maxLen: 7,
        type: "numeric"
    }, {
        name: "contact_policy_number",
        maxLen: 7,
        type: "alpha-numeric"
    }, {
        name: "contact_ssn",
        maxLen: 9,
        type: "numeric"
    }, {
        name: "patient_ssn",
        maxLen: 9,
        type: "numeric"
    }];

    for (var f = 0; f < fields.length; f++) {

        attachValidator(fields[f].name, fields[f].maxLen, fields[f].type);
    }

    function attachValidator(field, maxLen, type) {
        $j('#' + scope + '\\.' + field).on('input', function() {
            if (type == "numeric") {
                var digitsOnly = $j(this).val().replace(/\D/g, '');
                $j(this).val(digitsOnly);
            } else if (type == "alpha-numeric") {
                var alphaNumOnly = $j(this).val().replace(/\W/g, '');
                $j(this).val(alphaNumOnly);
            }

            if ($j(this).val().length > maxLen) {
                g_form.hideFieldMsg(field, true);
                g_form.showFieldMsg(field, 'Maximum (' + maxLen + ') ' + type + ' characters expected.', 'info');
            }

            $j(this).val($j(this).val().slice(0, maxLen));
        });
    }



}
